                            var tab = $(this).attr('data-tab');
                            
                            if($('#tutor-regist').hasClass("active")){
                                $('#tutor-regist').removeClass("active");
                            }
                            if(id == 'sub-tutoring-preference'){
                                var direct_type = $(this).attr('data-type'); 

                                if(direct_type == 'profile'){
                                    $('#profile').addClass('active in');
                                    $('#sub-profile').addClass('active');
                                }else if(direct_type == 'schedule'){
                                    $('.new-request-list').text('SCHEDULE');  
                                    //$("#summary-btn-tutor").text('Summary');
                                    //$("#summary-btn-tutor").attr("data-type","summary");
                                    $('#custom-timezone').attr("data-type","");
                                    $('#custom-timezone').attr("data-day","");

                                    if($('#btn-find-tutoring').hasClass('active')){
                                        $('#btn-find-tutoring').removeClass('active');
                                        $('#open-find-tutoring').css("display","none");
                                        $('.radio_tutor_search').attr('checked',false);                            
                                    }

                                    var day = $('#summary-btn-tutor').attr('data-day');
                                    var viewport = getViewport();
                                    if(viewport.width < 925){
                                        $('.box-schedule-left').css("display","block");
                                        $('#btn-open-calendar').css("display","block");
                                        if(viewport.width < 650){
                                            $('#tab-tutor-content .border-selectall').find('.col-md-4').css('width','52.88%');
                                        }else{
                                            $('#tab-tutor-content .border-selectall').find('.col-md-4').css('width','45.88%');
                                        }
                                    }else{
                                        $('#tab-tutor-content .border-selectall').find('.col-md-4').css('width','72.65%');
                                    }

                                    if($(".main-my-schedule-tutor").hasClass('active-reschedule')){
                                        $(".main-my-schedule-tutor").removeClass('active-reschedule');
                                    }

                                    if(!$(".main-my-schedule-tutor").hasClass('active-tab-schedule')){
                                        $(".main-my-schedule-tutor").addClass("active-tab-schedule");
                                    }

                                    get_list_schedule_tutor();

                                    get_scheduled_day_tutor(day);

                                    $(".main-my-schedule-tutor").css("display","block");
                                    $(".main-status-request").css("display","none");

                                    $('#tutoring-main').addClass('active in');
                                    $('#sub-schedule-li').addClass('active');
                                }else{
                                    get_update_info();                                                      
                                    $('span.placeholder').each(function () {
                                        var text = $(this).text();
                                        var font = $(this).css("font");                                
                                        if(text == 'Year:'){
                                            var offset = 11;
                                        }
                                        else{
                                            if (document.documentMode || /Edge/.test(navigator.userAgent)) {
                                                //console.log("Edge");
                                                var offset = getDistancePlace(text, "Edge");
                                            }else if (navigator.userAgent.search("Firefox") >= 0){
                                                var offset = getDistancePlace(text, "Firefox");
                                                //console.log("Firefox");
                                            }else{
                                                var offset = 18;
                                            }
                                        }
                                        var left = (getTextWidth(text,font) + offset);
                                        $(this).prev().css("padding-left",left+"px");
                                    });

                                    $('#updateinfo').addClass('active in');
                                    $('#sub-update-info').addClass('active');
                                }

                                $("#" + tab).removeClass("active in");
                                $("#" + id).removeClass("active");
                            }else{
                                $('#profile').addClass('active in');
                                $('#sub-profile').addClass('active');
                                $("#" + tab).removeClass("active in");
                                $("#" + id).removeClass("active");
                            }
                            $("#my-account-modal").scrollTop(0);
                        });

