                                    var ajax = "edit_desc_lesson"; 
                                    lid = child;
                                    child = 0;
                                }else{
                                    var ajax = "";
                                }

                                $.post(home_url + "/?r=ajax/" + ajax, {desc: text, lid: lid, child: child, old_desc: old_desc}, function (data) {
