                                    $('#detail-desc').text(desc);
                                    $("#btn-edit").text('Edit');
                                    $('#detail-desc').removeClass('hidden');
                                    $('#detail-input').addClass('hidden');
                                    
                                    updateDescList(type, gid, lid, child, desc, prefix);
                                });
                            }
