                        function getIconTutoring(type = ''){
                            var icon = '';
                            switch($.trim(type)) {
                                case 'English':
                                    icon = 'icon_ENGLISH.png';
                                    break;
                                case 'Math':
                                    icon = 'icon_MATH.png';
                                    break;
                                case 'History':
                                    icon = 'Type_History.png';
