                                case 'Science':
                                    icon = 'Type_Science.png';
                                    break;
                                case 'Music':
                                    icon = 'Type_Music.png';
                                    break;
                                case 'Other':
                                    icon = 'Type_Others.png';
                                    break;
                                default:
                                    icon = 'Type_Others.png';
                            }
                            return icon;
                        }

